client/src/pages/auth.tsx.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Server, ArrowRight, Loader2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/lib/auth";

const loginSchema = z.object({
  email: z.string().email("Invalid email address"),
  password: z.string().min(1, "Password is required"),
});

const registerSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters"),
  email: z.string().email("Invalid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  confirmPassword: z.string(),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type LoginFormData = z.infer<typeof loginSchema>;
type RegisterFormData = z.infer<typeof registerSchema>;

export default function AuthPage() {
  const [, setLocation] = useLocation();
  const { login, register } = useAuth();
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);
  const [activeTab, setActiveTab] = useState("login");

  const loginForm = useForm<LoginFormData>({
    resolver: zodResolver(loginSchema),
    defaultValues: { email: "", password: "" },
  });

  const registerForm = useForm<RegisterFormData>({
    resolver: zodResolver(registerSchema),
    defaultValues: { username: "", email: "", password: "", confirmPassword: "" },
  });

  const onLogin = async (data: LoginFormData) => {
    setIsLoading(true);
    try {
      await login(data.email, data.password);
      toast({ title: "Welcome back!", description: "You have successfully logged in." });
      setLocation("/");
    } catch (error: any) {
      toast({
        title: "Login failed",
        description: error.message || "Invalid credentials",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const onRegister = async (data: RegisterFormData) => {
    setIsLoading(true);
    try {
      await register(data.username, data.email, data.password);
      toast({ title: "Account created!", description: "Welcome to RDP Panel." });
      setLocation("/");
    } catch (error: any) {
      toast({
        title: "Registration failed",
        description: error.message || "Could not create account",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex">
      {/* Left side - Branding */}
      <div className="hidden lg:flex lg:w-1/2 bg-primary flex-col justify-between p-12">
        <div className="flex items-center gap-3">
          <div className="flex h-12 w-12 items-center justify-center rounded-xl bg-white/10 backdrop-blur">
            <Server className="h-6 w-6 text-white" />
          </div>
          <span className="text-2xl font-bold text-white">RDP Panel</span>
        </div>

        <div className="space-y-6">
          <h1 className="text-4xl font-bold text-white leading-tight">
            Premium Remote Desktop
            <br />
            <span className="text-white/80">Infrastructure</span>
          </h1>
          <p className="text-lg text-white/70 max-w-md">
            Deploy high-performance Windows VPS in Moscow and Amsterdam. 
            Enterprise-grade reliability with 24/7 support.
          </p>
          <div className="flex gap-8 pt-4">
            <div>
              <div className="text-3xl font-bold text-white">99.9%</div>
              <div className="text-sm text-white/60">Uptime SLA</div>
            </div>
            <div>
              <div className="text-3xl font-bold text-white">2</div>
              <div className="text-sm text-white/60">Data Centers</div>
            </div>
            <div>
              <div className="text-3xl font-bold text-white">24/7</div>
              <div className="text-sm text-white/60">Support</div>
            </div>
          </div>
        </div>

        <div className="text-sm text-white/50">
          Powered by OneDash Infrastructure
        </div>
      </div>

      {/* Right side - Auth forms */}
      <div className="flex-1 flex items-center justify-center p-8 bg-background">
        <div className="w-full max-w-md">
          {/* Mobile logo */}
          <div className="lg:hidden flex items-center justify-center gap-3 mb-8">
            <div className="flex h-12 w-12 items-center justify-center rounded-xl bg-primary">
              <Server className="h-6 w-6 text-primary-foreground" />
            </div>
            <span className="text-2xl font-bold">RDP Panel</span>
          </div>

          <Card className="border-0 shadow-none lg:border lg:shadow-sm">
            <CardHeader className="text-center pb-4">
              <CardTitle className="text-2xl">
                {activeTab === "login" ? "Welcome back" : "Create account"}
              </CardTitle>
              <CardDescription>
                {activeTab === "login"
                  ? "Enter your credentials to access your dashboard"
                  : "Get started with your RDP management panel"}
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Tabs value={activeTab} onValueChange={setActiveTab}>
                <TabsList className="grid w-full grid-cols-2 mb-6">
                  <TabsTrigger value="login" data-testid="tab-login">
                    Login
                  </TabsTrigger>
                  <TabsTrigger value="register" data-testid="tab-register">
                    Register
                  </TabsTrigger>
                </TabsList>

                <TabsContent value="login" className="space-y-4">
                  <Form {...loginForm}>
                    <form onSubmit={loginForm.handleSubmit(onLogin)} className="space-y-4">
                      <FormField
                        control={loginForm.control}
                        name="email"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Email</FormLabel>
                            <FormControl>
                              <Input
                                type="email"
                                placeholder="you@example.com"
                                data-testid="input-email"
                                {...field}
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={loginForm.control}
                        name="password"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Password</FormLabel>
                            <FormControl>
                              <Input
                                type="password"
                                placeholder="Enter your password"
                                data-testid="input-password"
                                {...field}
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <Button
                        type="submit"
                        className="w-full"
                        disabled={isLoading}
                        data-testid="button-login"
                      >
                        {isLoading ? (
                          <Loader2 className="h-4 w-4 animate-spin" />
                        ) : (
                          <>
                            Sign in
                            <ArrowRight className="h-4 w-4 ml-2" />
                          </>
                        )}
                      </Button>
                    </form>
                  </Form>
                </TabsContent>

                <TabsContent value="register" className="space-y-4">
                  <Form {...registerForm}>
                    <form onSubmit={registerForm.handleSubmit(onRegister)} className="space-y-4">
                      <FormField
                        control={registerForm.control}
                        name="username"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Username</FormLabel>
                            <FormControl>
                              <Input
                                placeholder="johndoe"
                                data-testid="input-username"
                                {...field}
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={registerForm.control}
                        name="email"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Email</FormLabel>
                            <FormControl>
                              <Input
                                type="email"
                                placeholder="you@example.com"
                                data-testid="input-reg-email"
                                {...field}
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={registerForm.control}
                        name="password"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Password</FormLabel>
                            <FormControl>
                              <Input
                                type="password"
                                placeholder="Create a password"
                                data-testid="input-reg-password"
                                {...field}
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={registerForm.control}
                        name="confirmPassword"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Confirm Password</FormLabel>
                            <FormControl>
                              <Input
                                type="password"
                                placeholder="Confirm your password"
                                data-testid="input-confirm-password"
                                {...field}
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <Button
                        type="submit"
                        className="w-full"
                        disabled={isLoading}
                        data-testid="button-register"
                      >
                        {isLoading ? (
                          <Loader2 className="h-4 w-4 animate-spin" />
                        ) : (
                          <>
                            Create account
                            <ArrowRight className="h-4 w-4 ml-2" />
                          </>
                        )}
                      </Button>
                    </form>
                  </Form>
                </TabsContent>
              </Tabs>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/pages/admin/users.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { 
  Users, 
  Search, 
  RefreshCw, 
  MoreVertical,
  Ban,
  CheckCircle,
  Trash2,
  Shield,
  User,
  Loader2,
  Mail,
  Server,
  Eye,
  History,
  Key,
} from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";

interface VpsData {
  id: string;
  os: string;
  ipAddress: string | null;
  status: string;
  createdAt: string;
}

interface LoginLogData {
  id: string;
  ipAddress: string;
  userAgent: string | null;
  createdAt: string;
}

interface UserData {
  id: string;
  username: string;
  email: string;
  role: string;
  status: string;
  balance: string;
  registrationIp: string | null;
  suspendedUntil: string | null;
  ordersCount: number;
  vpsCount: number;
  vpsList: VpsData[];
  createdAt: string;
}

export default function AdminUsers() {
  const { toast } = useToast();
  const [searchQuery, setSearchQuery] = useState("");
  const [actionDialog, setActionDialog] = useState<{
    open: boolean;
    type: "suspend" | "activate" | "delete" | "email" | "vps" | "logs" | "password" | null;
    userId: string | null;
    username: string | null;
    currentEmail?: string;
    vpsList?: VpsData[];
  }>({ open: false, type: null, userId: null, username: null });
  const [newEmail, setNewEmail] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [suspendDuration, setSuspendDuration] = useState("permanent");
  const [loginLogs, setLoginLogs] = useState<LoginLogData[]>([]);
  const [logsLoading, setLogsLoading] = useState(false);

  const { data: users, isLoading, refetch, isRefetching } = useQuery<UserData[]>({
    queryKey: ["/api/admin/users"],
  });

  const updateStatusMutation = useMutation({
    mutationFn: async ({ userId, status, suspendedUntil }: { userId: string; status: string; suspendedUntil?: string | null }) => {
      return apiRequest("PATCH", `/api/admin/users/${userId}/status`, { status, suspendedUntil });
    },
    onSuccess: () => {
      toast({ title: "User updated", description: "User status has been updated." });
      setActionDialog({ open: false, type: null, userId: null, username: null });
      setSuspendDuration("permanent");
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
    },
    onError: (error: Error) => {
      toast({ title: "Update failed", description: error.message, variant: "destructive" });
    },
  });

  const updateEmailMutation = useMutation({
    mutationFn: async ({ userId, email }: { userId: string; email: string }) => {
      return apiRequest("PATCH", `/api/admin/users/${userId}/email`, { email });
    },
    onSuccess: () => {
      toast({ title: "Email updated", description: "User email has been updated." });
      setActionDialog({ open: false, type: null, userId: null, username: null });
      setNewEmail("");
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
    },
    onError: (error: Error) => {
      toast({ title: "Update failed", description: error.message, variant: "destructive" });
    },
  });

  const updatePasswordMutation = useMutation({
    mutationFn: async ({ userId, password }: { userId: string; password: string }) => {
      return apiRequest("PATCH", `/api/admin/users/${userId}/password`, { password });
    },
    onSuccess: () => {
      toast({ title: "Password updated", description: "User password has been updated." });
      setActionDialog({ open: false, type: null, userId: null, username: null });
      setNewPassword("");
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
    },
    onError: (error: Error) => {
      toast({ title: "Update failed", description: error.message, variant: "destructive" });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (userId: string) => {
      return apiRequest("DELETE", `/api/admin/users/${userId}`);
    },
    onSuccess: () => {
      toast({ title: "User deleted", description: "User has been removed from the system." });
      setActionDialog({ open: false, type: null, userId: null, username: null });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
    },
    onError: (error: Error) => {
      toast({ title: "Delete failed", description: error.message, variant: "destructive" });
    },
  });

  const filteredUsers = users?.filter(
    (user) =>
      user.username.toLowerCase().includes(searchQuery.toLowerCase()) ||
      user.email.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });
  };

  const formatDateTime = (dateString: string) => {
    return new Date(dateString).toLocaleString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const getSuspendedUntilDate = (duration: string): string | null => {
    if (duration === "permanent") return null;
    const now = new Date();
    switch (duration) {
      case "1day":
        now.setDate(now.getDate() + 1);
        break;
      case "3days":
        now.setDate(now.getDate() + 3);
        break;
      case "7days":
        now.setDate(now.getDate() + 7);
        break;
      case "30days":
        now.setDate(now.getDate() + 30);
        break;
      case "90days":
        now.setDate(now.getDate() + 90);
        break;
      default:
        return null;
    }
    return now.toISOString();
  };

  const handleAction = () => {
    if (!actionDialog.userId) return;

    if (actionDialog.type === "suspend") {
      const suspendedUntil = getSuspendedUntilDate(suspendDuration);
      updateStatusMutation.mutate({ userId: actionDialog.userId, status: "suspended", suspendedUntil });
    } else if (actionDialog.type === "activate") {
      updateStatusMutation.mutate({ userId: actionDialog.userId, status: "active", suspendedUntil: null });
    } else if (actionDialog.type === "delete") {
      deleteMutation.mutate(actionDialog.userId);
    } else if (actionDialog.type === "email") {
      updateEmailMutation.mutate({ userId: actionDialog.userId, email: newEmail });
    } else if (actionDialog.type === "password") {
      updatePasswordMutation.mutate({ userId: actionDialog.userId, password: newPassword });
    }
  };

  const getStatusBadge = (user: UserData) => {
    if (user.status === "active") {
      return (
        <Badge
          variant="outline"
          className="bg-emerald-500/15 text-emerald-600 dark:text-emerald-400 border-emerald-500/20"
        >
          <CheckCircle className="h-3 w-3 mr-1" />
          active
        </Badge>
      );
    }

    if (user.suspendedUntil) {
      const suspendedUntilDate = new Date(user.suspendedUntil);
      const now = new Date();
      if (suspendedUntilDate > now) {
        return (
          <div className="flex flex-col gap-1">
            <Badge
              variant="outline"
              className="bg-red-500/15 text-red-600 dark:text-red-400 border-red-500/20"
            >
              <Ban className="h-3 w-3 mr-1" />
              suspended
            </Badge>
            <span className="text-xs text-muted-foreground">
              Until {formatDate(user.suspendedUntil)}
            </span>
          </div>
        );
      }
    }

    return (
      <Badge
        variant="outline"
        className="bg-red-500/15 text-red-600 dark:text-red-400 border-red-500/20"
      >
        <Ban className="h-3 w-3 mr-1" />
        suspended
      </Badge>
    );
  };

  return (
    <div className="space-y-6">
      <div className="flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
        <div>
          <h1 className="text-3xl font-semibold">User Management</h1>
          <p className="text-muted-foreground mt-1">
            View and manage all platform users
          </p>
        </div>
        <div className="flex items-center gap-3">
          <div className="relative w-64">
            <Search className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground" />
            <Input
              placeholder="Search users, email, IP..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-9"
              data-testid="input-search-users"
            />
          </div>
          <Button
            variant="outline"
            size="icon"
            onClick={() => refetch()}
            disabled={isRefetching}
            data-testid="button-refresh-users"
          >
            <RefreshCw className={`h-4 w-4 ${isRefetching ? "animate-spin" : ""}`} />
          </Button>
        </div>
      </div>

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Users className="h-5 w-5" />
            All Users
          </CardTitle>
          <CardDescription>
            {filteredUsers?.length || 0} user{filteredUsers?.length !== 1 ? "s" : ""} found
          </CardDescription>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="space-y-4">
              {[1, 2, 3, 4, 5].map((i) => (
                <div key={i} className="flex items-center gap-4 p-4 rounded-lg border">
                  <Skeleton className="h-10 w-10 rounded-full" />
                  <div className="flex-1 space-y-2">
                    <Skeleton className="h-4 w-32" />
                    <Skeleton className="h-3 w-48" />
                  </div>
                  <Skeleton className="h-8 w-8" />
                </div>
              ))}
            </div>
          ) : filteredUsers && filteredUsers.length > 0 ? (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>User</TableHead>
                    <TableHead>Role</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead className="text-right">Balance</TableHead>
                    <TableHead className="text-right">Orders</TableHead>
                    <TableHead className="text-right">RDP/VPS</TableHead>
                    <TableHead>Joined</TableHead>
                    <TableHead className="w-12"></TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {filteredUsers.map((user) => (
                    <TableRow key={user.id} data-testid={`user-row-${user.id}`}>
                      <TableCell>
                        <div className="flex items-center gap-3">
                          <div className="flex h-10 w-10 items-center justify-center rounded-full bg-primary/10">
                            {user.role === "admin" ? (
                              <Shield className="h-5 w-5 text-primary" />
                            ) : (
                              <User className="h-5 w-5 text-primary" />
                            )}
                          </div>
                          <div>
                            <div className="font-medium">{user.username}</div>
                            <div className="text-sm text-muted-foreground">{user.email}</div>
                          </div>
                        </div>
                      </TableCell>
                      <TableCell>
                        <Badge variant={user.role === "admin" ? "default" : "secondary"}>
                          {user.role}
                        </Badge>
                      </TableCell>
                      <TableCell>
                        {getStatusBadge(user)}
                      </TableCell>
                      <TableCell className="text-right font-mono">
                        ${parseFloat(user.balance).toFixed(2)}
                      </TableCell>
                      <TableCell className="text-right">{user.ordersCount}</TableCell>
                      <TableCell className="text-right">
                        {user.vpsCount > 0 ? (
                          <Button
                            variant="ghost"
                            size="sm"
                            className="h-auto p-1"
                            onClick={() =>
                              setActionDialog({
                                open: true,
                                type: "vps",
                                userId: user.id,
                                username: user.username,
                                vpsList: user.vpsList,
                              })
                            }
                          >
                            <Server className="h-3 w-3 mr-1" />
                            {user.vpsCount}
                          </Button>
                        ) : (
                          <span className="text-muted-foreground">0</span>
                        )}
                      </TableCell>
                      <TableCell className="text-muted-foreground">
                        {formatDate(user.createdAt)}
                      </TableCell>
                      <TableCell>
                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="ghost" size="icon" data-testid={`button-user-menu-${user.id}`}>
                              <MoreVertical className="h-4 w-4" />
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end">
                            <DropdownMenuItem
                              onClick={() => {
                                setNewEmail(user.email);
                                setActionDialog({
                                  open: true,
                                  type: "email",
                                  userId: user.id,
                                  username: user.username,
                                  currentEmail: user.email,
                                });
                              }}
                            >
                              <Mail className="h-4 w-4 mr-2" />
                              Change Email
                            </DropdownMenuItem>
                            <DropdownMenuItem
                              onClick={() => {
                                setNewPassword("");
                                setActionDialog({
                                  open: true,
                                  type: "password",
                                  userId: user.id,
                                  username: user.username,
                                });
                              }}
                            >
                              <Key className="h-4 w-4 mr-2" />
                              Change Password
                            </DropdownMenuItem>
                            {user.vpsList && user.vpsList.length > 0 && (
                              <DropdownMenuItem
                                onClick={() =>
                                  setActionDialog({
                                    open: true,
                                    type: "vps",
                                    userId: user.id,
                                    username: user.username,
                                    vpsList: user.vpsList,
                                  })
                                }
                              >
                                <Eye className="h-4 w-4 mr-2" />
                                View RDP/VPS
                              </DropdownMenuItem>
                            )}
                            <DropdownMenuItem
                              onClick={async () => {
                                setLogsLoading(true);
                                setActionDialog({
                                  open: true,
                                  type: "logs",
                                  userId: user.id,
                                  username: user.username,
                                });
                                try {
                                  const res = await fetch(`/api/admin/users/${user.id}/login-logs`, { credentials: "include" });
                                  const data = await res.json();
                                  setLoginLogs(data);
                                } catch (e) {
                                  setLoginLogs([]);
                                } finally {
                                  setLogsLoading(false);
                                }
                              }}
                            >
                              <History className="h-4 w-4 mr-2" />
                              Login History
                            </DropdownMenuItem>
                            <DropdownMenuSeparator />
                            {user.status === "active" ? (
                              <DropdownMenuItem
                                onClick={() =>
                                  setActionDialog({
                                    open: true,
                                    type: "suspend",
                                    userId: user.id,
                                    username: user.username,
                                  })
                                }
                              >
                                <Ban className="h-4 w-4 mr-2" />
                                Suspend User
                              </DropdownMenuItem>
                            ) : (
                              <DropdownMenuItem
                                onClick={() =>
                                  setActionDialog({
                                    open: true,
                                    type: "activate",
                                    userId: user.id,
                                    username: user.username,
                                  })
                                }
                              >
                                <CheckCircle className="h-4 w-4 mr-2" />
                                Activate User
                              </DropdownMenuItem>
                            )}
                            <DropdownMenuSeparator />
                            <DropdownMenuItem
                              className="text-destructive"
                              onClick={() =>
                                setActionDialog({
                                  open: true,
                                  type: "delete",
                                  userId: user.id,
                                  username: user.username,
                                })
                              }
                            >
                              <Trash2 className="h-4 w-4 mr-2" />
                              Delete User
                            </DropdownMenuItem>
                          </DropdownMenuContent>
                        </DropdownMenu>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>
          ) : (
            <div className="flex flex-col items-center justify-center py-16 text-center">
              <div className="flex h-16 w-16 items-center justify-center rounded-full bg-muted mb-4">
                <Users className="h-8 w-8 text-muted-foreground" />
              </div>
              <h3 className="font-medium mb-1">No users found</h3>
              <p className="text-sm text-muted-foreground">
                {searchQuery ? "Try adjusting your search query" : "No users have registered yet"}
              </p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Action Confirmation Dialog */}
      <Dialog
        open={actionDialog.open && actionDialog.type !== "vps" && actionDialog.type !== "email" && actionDialog.type !== "password" && actionDialog.type !== "logs"}
        onOpenChange={(open) => {
          if (!open) {
            setActionDialog({ open: false, type: null, userId: null, username: null });
            setSuspendDuration("permanent");
          }
        }}
      >
        <DialogContent>
          <DialogHeader>
            <DialogTitle>
              {actionDialog.type === "suspend" && "Suspend User"}
              {actionDialog.type === "activate" && "Activate User"}
              {actionDialog.type === "delete" && "Delete User"}
            </DialogTitle>
            <DialogDescription>
              {actionDialog.type === "suspend" &&
                `Are you sure you want to suspend ${actionDialog.username}? They will not be able to access their account.`}
              {actionDialog.type === "activate" &&
                `Are you sure you want to activate ${actionDialog.username}? They will regain access to their account.`}
              {actionDialog.type === "delete" &&
                `Are you sure you want to delete ${actionDialog.username}? This action cannot be undone.`}
            </DialogDescription>
          </DialogHeader>

          {actionDialog.type === "suspend" && (
            <div className="space-y-3 py-4">
              <Label>Suspension Duration</Label>
              <Select value={suspendDuration} onValueChange={setSuspendDuration}>
                <SelectTrigger>
                  <SelectValue placeholder="Select duration" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="1day">1 Day</SelectItem>
                  <SelectItem value="3days">3 Days</SelectItem>
                  <SelectItem value="7days">7 Days</SelectItem>
                  <SelectItem value="30days">30 Days</SelectItem>
                  <SelectItem value="90days">90 Days</SelectItem>
                  <SelectItem value="permanent">Permanent</SelectItem>
                </SelectContent>
              </Select>
            </div>
          )}

          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setActionDialog({ open: false, type: null, userId: null, username: null });
                setSuspendDuration("permanent");
              }}
            >
              Cancel
            </Button>
            <Button
              variant={actionDialog.type === "delete" ? "destructive" : "default"}
              onClick={handleAction}
              disabled={updateStatusMutation.isPending || deleteMutation.isPending}
              data-testid="button-confirm-action"
            >
              {(updateStatusMutation.isPending || deleteMutation.isPending) && (
                <Loader2 className="h-4 w-4 mr-2 animate-spin" />
              )}
              {actionDialog.type === "suspend" && "Suspend"}
              {actionDialog.type === "activate" && "Activate"}
              {actionDialog.type === "delete" && "Delete"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Email Change Dialog */}
      <Dialog
        open={actionDialog.open && actionDialog.type === "email"}
        onOpenChange={(open) => {
          if (!open) {
            setActionDialog({ open: false, type: null, userId: null, username: null });
            setNewEmail("");
          }
        }}
      >
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Change Email</DialogTitle>
            <DialogDescription>
              Update the email address for {actionDialog.username}.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="email">New Email Address</Label>
              <Input
                id="email"
                type="email"
                placeholder="new@email.com"
                value={newEmail}
                onChange={(e) => setNewEmail(e.target.value)}
              />
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setActionDialog({ open: false, type: null, userId: null, username: null });
                setNewEmail("");
              }}
            >
              Cancel
            </Button>
            <Button
              onClick={handleAction}
              disabled={updateEmailMutation.isPending || !newEmail || newEmail === actionDialog.currentEmail}
            >
              {updateEmailMutation.isPending && (
                <Loader2 className="h-4 w-4 mr-2 animate-spin" />
              )}
              Update Email
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Password Change Dialog */}
      <Dialog
        open={actionDialog.open && actionDialog.type === "password"}
        onOpenChange={(open) => {
          if (!open) {
            setActionDialog({ open: false, type: null, userId: null, username: null });
            setNewPassword("");
          }
        }}
      >
        <DialogContent>
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <Key className="h-5 w-5" />
              Change Password
            </DialogTitle>
            <DialogDescription>
              Set a new password for {actionDialog.username}.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="password">New Password</Label>
              <Input
                id="password"
                type="password"
                placeholder="Enter new password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
              />
              <p className="text-xs text-muted-foreground">
                Password must be at least 6 characters long.
              </p>
            </div>
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setActionDialog({ open: false, type: null, userId: null, username: null });
                setNewPassword("");
              }}
            >
              Cancel
            </Button>
            <Button
              onClick={handleAction}
              disabled={updatePasswordMutation.isPending || newPassword.length < 6}
            >
              {updatePasswordMutation.isPending && (
                <Loader2 className="h-4 w-4 mr-2 animate-spin" />
              )}
              Update Password
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* VPS List Dialog */}
      <Dialog
        open={actionDialog.open && actionDialog.type === "vps"}
        onOpenChange={(open) => {
          if (!open) {
            setActionDialog({ open: false, type: null, userId: null, username: null });
          }
        }}
      >
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <Server className="h-5 w-5" />
              RDP/VPS for {actionDialog.username}
            </DialogTitle>
            <DialogDescription>
              List of all RDP/VPS instances owned by this user.
            </DialogDescription>
          </DialogHeader>
          <div className="py-4">
            {actionDialog.vpsList && actionDialog.vpsList.length > 0 ? (
              <div className="overflow-x-auto">
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>OS</TableHead>
                      <TableHead>IP Address</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Created</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {actionDialog.vpsList.map((vps) => (
                      <TableRow key={vps.id}>
                        <TableCell className="font-medium">{vps.os}</TableCell>
                        <TableCell>
                          {vps.ipAddress ? (
                            <code className="text-xs bg-muted px-1.5 py-0.5 rounded">
                              {vps.ipAddress}
                            </code>
                          ) : (
                            <span className="text-muted-foreground">Not assigned</span>
                          )}
                        </TableCell>
                        <TableCell>
                          <Badge
                            variant="outline"
                            className={
                              vps.status === "runned"
                                ? "bg-emerald-500/15 text-emerald-600 dark:text-emerald-400 border-emerald-500/20"
                                : "bg-amber-500/15 text-amber-600 dark:text-amber-400 border-amber-500/20"
                            }
                          >
                            {vps.status}
                          </Badge>
                        </TableCell>
                        <TableCell className="text-muted-foreground">
                          {formatDateTime(vps.createdAt)}
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>
            ) : (
              <div className="text-center py-8 text-muted-foreground">
                No RDP/VPS instances found.
              </div>
            )}
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() =>
                setActionDialog({ open: false, type: null, userId: null, username: null })
              }
            >
              Close
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Login Logs Dialog */}
      <Dialog
        open={actionDialog.open && actionDialog.type === "logs"}
        onOpenChange={(open) => {
          if (!open) {
            setActionDialog({ open: false, type: null, userId: null, username: null });
            setLoginLogs([]);
          }
        }}
      >
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <History className="h-5 w-5" />
              Login History for {actionDialog.username}
            </DialogTitle>
            <DialogDescription>
              Recent login activity including IP addresses and timestamps.
            </DialogDescription>
          </DialogHeader>
          <div className="py-4 max-h-96 overflow-y-auto">
            {logsLoading ? (
              <div className="space-y-3">
                {[1, 2, 3].map((i) => (
                  <Skeleton key={i} className="h-12 w-full" />
                ))}
              </div>
            ) : loginLogs.length > 0 ? (
              <div className="overflow-x-auto">
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>IP Address</TableHead>
                      <TableHead>User Agent</TableHead>
                      <TableHead>Date & Time</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {loginLogs.map((log) => (
                      <TableRow key={log.id}>
                        <TableCell>
                          <code className="text-xs bg-muted px-1.5 py-0.5 rounded">
                            {log.ipAddress}
                          </code>
                        </TableCell>
                        <TableCell className="max-w-xs truncate text-sm text-muted-foreground">
                          {log.userAgent || "-"}
                        </TableCell>
                        <TableCell className="text-muted-foreground">
                          {formatDateTime(log.createdAt)}
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>
            ) : (
              <div className="text-center py-8 text-muted-foreground">
                No login history found.
              </div>
            )}
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setActionDialog({ open: false, type: null, userId: null, username: null });
                setLoginLogs([]);
              }}
            >
              Close
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

