}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, boolean, timestamp, decimal, jsonb } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Users table with role-based access
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  email: text("email").notNull().unique(),
  password: text("password").notNull(),
  role: text("role").notNull().default("customer"), // "customer" | "admin"
  status: text("status").notNull().default("active"), // "active" | "suspended"
  balance: decimal("balance", { precision: 10, scale: 2 }).notNull().default("0"),
  registrationIp: text("registration_ip"),
  suspendedUntil: timestamp("suspended_until"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  email: true,
  password: true,
}).extend({
  username: z.string().min(3, "Username must be at least 3 characters"),
  email: z.string().email("Invalid email address"),
  password: z.string().min(6, "Password must be at least 6 characters"),
});

export const loginSchema = z.object({
  email: z.string().email("Invalid email address"),
  password: z.string().min(1, "Password is required"),
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type LoginInput = z.infer<typeof loginSchema>;

// Orders table - tracks customer orders
export const orders = pgTable("orders", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().references(() => users.id),
  onedashOrderId: integer("onedash_order_id"), // ID from OneDash API
  tariffId: integer("tariff_id").notNull(),
  tariffName: text("tariff_name").notNull(),
  location: text("location").notNull(), // "msk" | "ams"
  period: integer("period").notNull(), // days
  count: integer("count").notNull().default(1),
  totalPrice: decimal("total_price", { precision: 10, scale: 2 }).notNull(),
  status: text("status").notNull().default("pending"), // "pending" | "active" | "expired" | "cancelled"
  finishDate: timestamp("finish_date"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertOrderSchema = createInsertSchema(orders).omit({
  id: true,
  createdAt: true,
  onedashOrderId: true,
  finishDate: true,
  status: true,
});

export type InsertOrder = z.infer<typeof insertOrderSchema>;
export type Order = typeof orders.$inferSelect;

// VPS instances table
export const vpsInstances = pgTable("vps_instances", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  orderId: varchar("order_id").notNull().references(() => orders.id),
  userId: varchar("user_id").notNull().references(() => users.id),
  onedashVpsId: integer("onedash_vps_id"),
  os: text("os").notNull(),
  ipAddress: text("ip_address"),
  rdpUsername: text("rdp_username").default("Administrator"),
  rdpPassword: text("rdp_password"),
  status: text("status").notNull().default("not_runned"), // "runned" | "not_runned" | "cloning"
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export const insertVpsSchema = createInsertSchema(vpsInstances).omit({
  id: true,
  createdAt: true,
});

export type InsertVps = z.infer<typeof insertVpsSchema>;
export type VpsInstance = typeof vpsInstances.$inferSelect;

// Pricing rules - admin-defined markup on OneDash prices
export const pricingRules = pgTable("pricing_rules", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  tariffId: integer("tariff_id").notNull(),
  location: text("location").notNull(), // "msk" | "ams"
  markupType: text("markup_type").notNull().default("percentage"), // "percentage" | "fixed"
  markupValue: decimal("markup_value", { precision: 10, scale: 2 }).notNull().default("20"), // e.g., 20% or $20
  isActive: boolean("is_active").notNull().default(true),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

export const insertPricingRuleSchema = createInsertSchema(pricingRules).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export type InsertPricingRule = z.infer<typeof insertPricingRuleSchema>;
export type PricingRule = typeof pricingRules.$inferSelect;

// Sessions for authentication
export const sessions = pgTable("sessions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().references(() => users.id),
  token: text("token").notNull().unique(),
  expiresAt: timestamp("expires_at").notNull(),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Login logs for IP tracking
export const loginLogs = pgTable("login_logs", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().references(() => users.id),
  ipAddress: text("ip_address").notNull(),
  userAgent: text("user_agent"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

export type LoginLog = typeof loginLogs.$inferSelect;

// Transactions table for payment tracking
export const transactions = pgTable("transactions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().references(() => users.id),
  type: text("type").notNull(), // "topup" | "purchase"
  amount: decimal("amount", { precision: 10, scale: 2 }).notNull(),
  currency: text("currency").notNull().default("USD"),
  status: text("status").notNull().default("pending"), // "pending" | "completed" | "failed" | "cancelled"
  paymentMethod: text("payment_method"), // "maxelpay" | "manual"
  externalId: text("external_id"), // MaxelPay order ID
  metadata: jsonb("metadata"), // Additional payment data
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

export const insertTransactionSchema = createInsertSchema(transactions).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export type InsertTransaction = z.infer<typeof insertTransactionSchema>;
export type Transaction = typeof transactions.$inferSelect;

// Create VPS request schema for API
export const createVpsRequestSchema = z.object({
  tariffId: z.number().int().positive(),
  location: z.enum(["msk", "ams"]),
  system: z.string().min(1),
  period: z.number().int().min(7).max(360),
  count: z.number().int().min(1).max(10).default(1),
  additionalOptions: z.object({
    staticIp: z.boolean().default(false),
    nvme: z.boolean().default(false),
    backup: z.boolean().default(false),
  }).optional(),
  processor: z.enum(["intel", "amd"]).default("intel"),
});

export type CreateVpsRequest = z.infer<typeof createVpsRequestSchema>;

// Platform settings table for API keys (stored securely in database)
export const platformSettings = pgTable("platform_settings", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  key: text("key").notNull().unique(),
  value: text("value").notNull(),
  description: text("description"),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

export type PlatformSetting = typeof platformSettings.$inferSelect;

// OneDash API response types
export interface OneDashTariff {
  id: number;
  name: string;
  msk_prices: Array<{ price: number; discount: number; period: number }>;
  ams_prices: Array<{ price: number; discount: number; period: number }>;
  currency: string;
  config_info: { cpu: number; ram: number; hard: number };
}

export interface OneDashOrder {
  order_id: number;
  tariff: { id: number; name: string };
  location: string;
  vps_list: Array<{
    id: number;
    os: string;
    vps_ip: string;
    vps_status: "runned" | "not_runned" | "cloning";
  }>;
  finish_time: {
    epoch: number;
    days_remaining: number;
    date: string;
  };
}

export interface OneDashBalance {
  balance: number;
  currency: string;
}

server/maxelpay.ts.content:
import { createHash, createHmac } from "crypto";
import { storage } from "./storage";

const MAXELPAY_API_URL = "https://api.maxelpay.com/api/v1";

interface PaymentPayload {
  orderID: string;
  amount: string;
  currency: string;
  timestamp: number;
  userName: string;
  siteName: string;
  userEmail: string;
  redirectUrl: string;
  websiteUrl: string;
  cancelUrl: string;
  webhookUrl: string;
}

interface PaymentResponse {
  success: boolean;
  paymentUrl?: string;
  error?: string;
}

class MaxelPayService {
  private async getApiKey(): Promise<string> {
    const dbKey = await storage.getSetting("MAXELPAY_API_KEY");
    const apiKey = dbKey || process.env.MAXELPAY_API_KEY;
    if (!apiKey) {
      throw new Error("MaxelPay API key not configured. Please set it in Admin Settings.");
    }
    return apiKey;
  }

  private async getApiSecret(): Promise<string> {
    const dbSecret = await storage.getSetting("MAXELPAY_API_SECRET");
    const apiSecret = dbSecret || process.env.MAXELPAY_API_SECRET;
    if (!apiSecret) {
      throw new Error("MaxelPay API secret not configured. Please set it in Admin Settings.");
    }
    return apiSecret;
  }

  private async generateSignature(payload: string): Promise<string> {
    const secret = await this.getApiSecret();
    return createHmac("sha256", secret)
      .update(payload)
      .digest("hex");
  }

  async createPayment(params: {
    orderId: string;
    amount: number;
    currency: string;
    userName: string;
    userEmail: string;
    baseUrl: string;
  }): Promise<PaymentResponse> {
    const payload: PaymentPayload = {
      orderID: params.orderId,
      amount: params.amount.toString(),
      currency: params.currency,
      timestamp: Math.floor(Date.now() / 1000),
      userName: params.userName,
      siteName: "RDP Panel",
      userEmail: params.userEmail,
      redirectUrl: `${params.baseUrl}/dashboard?payment=success`,
      websiteUrl: params.baseUrl,
      cancelUrl: `${params.baseUrl}/dashboard?payment=cancelled`,
      webhookUrl: `${params.baseUrl}/api/payments/webhook`,
    };

    const payloadString = JSON.stringify(payload);
    const signature = await this.generateSignature(payloadString);
    const apiKey = await this.getApiKey();

    try {
      const response = await fetch(`${MAXELPAY_API_URL}/payment/create`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "X-API-Key": apiKey,
          "X-Signature": signature,
        },
        body: payloadString,
      });

      const data = await response.json();

      if (data.success && data.paymentUrl) {
        return {
          success: true,
          paymentUrl: data.paymentUrl,
        };
      }

      return {
        success: false,
        error: data.message || "Failed to create payment",
      };
    } catch (error: any) {
      return {
        success: false,
        error: error.message || "Payment service unavailable",
      };
    }
  }

  async verifyWebhook(payload: string, signature: string): Promise<boolean> {
    const expectedSignature = await this.generateSignature(payload);
    return expectedSignature === signature;
  }
}

export const maxelpayService = new MaxelPayService();

server/nowpayments.ts.content:
import { storage } from "./storage";
import crypto from "crypto";

const NOWPAYMENTS_API_URL = "https://api.nowpayments.io/v1";
const NOWPAYMENTS_SANDBOX_URL = "https://api-sandbox.nowpayments.io/v1";

interface PaymentResponse {
  success: boolean;
  paymentUrl?: string;
  paymentId?: string;
  payAddress?: string;
  payAmount?: number;
  payCurrency?: string;
  error?: string;
}

interface DirectPaymentResponse {
  success: boolean;
  paymentId?: string;
  payAddress?: string;
  payAmount?: string;
  payCurrency?: string;
  expiresAt?: string;
  qrCodeUrl?: string;
  status?: string;
  error?: string;
}

interface NowPaymentsCreateResponse {
  payment_id: string;
  payment_status: string;
  pay_address: string;
  price_amount: number;
  price_currency: string;
  pay_amount: number;
  pay_currency: string;
  order_id: string;
  order_description: string;
  purchase_id: string;
  created_at: string;
  updated_at: string;
  expiration_estimate_date: string;
  invoice_url?: string;
}

class NowPaymentsService {
  private async getApiKey(): Promise<string> {
    const dbKey = await storage.getSetting("NOWPAYMENTS_API_KEY");
    const apiKey = dbKey || process.env.NOWPAYMENTS_API_KEY;
    if (!apiKey) {
      throw new Error("NOWPayments API key not configured. Please set it in Admin Settings.");
    }
    return apiKey;
  }

  private async isTestMode(): Promise<boolean> {
    const testMode = await storage.getSetting("NOWPAYMENTS_TEST_MODE");
    return testMode === "true";
  }

  private async getApiUrl(): Promise<string> {
    const isTest = await this.isTestMode();
    return isTest ? NOWPAYMENTS_SANDBOX_URL : NOWPAYMENTS_API_URL;
  }

  async createPayment(params: {
    orderId: string;
    amount: number;
    currency: string;
    userName: string;
    userEmail: string;
    baseUrl: string;
  }): Promise<PaymentResponse> {
    const apiKey = await this.getApiKey();
    const apiUrl = await this.getApiUrl();

    try {
      const response = await fetch(`${apiUrl}/invoice`, {
        method: "POST",
        headers: {
          "x-api-key": apiKey,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          price_amount: params.amount,
          price_currency: params.currency.toLowerCase(),
          order_id: params.orderId,
          order_description: `Balance top-up for ${params.userName}`,
          ipn_callback_url: `${params.baseUrl}/api/payments/webhook`,
          success_url: `${params.baseUrl}/dashboard?payment=success`,
          cancel_url: `${params.baseUrl}/dashboard?payment=cancelled`,
        }),
      });

      const contentType = response.headers.get("content-type") || "";

      if (!contentType.includes("application/json")) {
        const text = await response.text();
        console.error("NOWPayments returned non-JSON response:", text.substring(0, 500));
        return {
          success: false,
          error: `Payment provider returned an error (status ${response.status}). Please check API credentials in Admin Settings.`,
        };
      }

      const data = await response.json();
      console.log("NOWPayments response status:", response.status);
      console.log("NOWPayments response data:", JSON.stringify(data, null, 2));

      if (!response.ok) {
        console.error("NOWPayments error response:", data);
        return {
          success: false,
          error: data.message || data.error || `Payment provider error (status ${response.status})`,
        };
      }

      if (data.invoice_url) {
        return {
          success: true,
          paymentUrl: data.invoice_url,
          paymentId: data.id,
        };
      }

      return {
        success: false,
        error: data.message || data.error || "Failed to create payment - no invoice URL returned",
      };
    } catch (error: any) {
      console.error("NOWPayments error:", error);
      return {
        success: false,
        error: error.message || "Payment service unavailable",
      };
    }
  }

  async getAvailableCurrencies(): Promise<string[]> {
    try {
      const apiKey = await this.getApiKey();
      const apiUrl = await this.getApiUrl();

      const response = await fetch(`${apiUrl}/currencies`, {
        method: "GET",
        headers: {
          "x-api-key": apiKey,
        },
      });

      if (!response.ok) {
        return ["btc", "eth", "ltc", "usdt", "trx"];
      }

      const data = await response.json();
      return data.currencies || ["btc", "eth", "ltc", "usdt", "trx"];
    } catch (error) {
      console.error("Error fetching currencies:", error);
      return ["btc", "eth", "ltc", "usdt", "trx"];
    }
  }

  async getMinimumPaymentAmount(payCurrency: string, priceCurrency: string = "usd"): Promise<number> {
    try {
      const apiKey = await this.getApiKey();
      const apiUrl = await this.getApiUrl();

      const response = await fetch(`${apiUrl}/min-amount?currency_from=${payCurrency}&currency_to=${priceCurrency}`, {
        method: "GET",
        headers: {
          "x-api-key": apiKey,
        },
      });

      if (!response.ok) {
        return 0;
      }

      const data = await response.json();
      return data.min_amount || 0;
    } catch (error) {
      return 0;
    }
  }

  async createDirectPayment(params: {
    orderId: string;
    amount: number;
    currency: string;
    payCurrency: string;
    baseUrl: string;
  }): Promise<DirectPaymentResponse> {
    const apiKey = await this.getApiKey();
    const apiUrl = await this.getApiUrl();

    try {
      const response = await fetch(`${apiUrl}/payment`, {
        method: "POST",
        headers: {
          "x-api-key": apiKey,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          price_amount: params.amount,
          price_currency: params.currency.toLowerCase(),
          pay_currency: params.payCurrency.toLowerCase(),
          order_id: params.orderId,
          order_description: `Balance top-up - $${params.amount}`,
          ipn_callback_url: `${params.baseUrl}/api/payments/webhook`,
          is_fixed_rate: false,
        }),
      });

      const contentType = response.headers.get("content-type") || "";

      if (!contentType.includes("application/json")) {
        const text = await response.text();
        console.error("NOWPayments returned non-JSON response:", text.substring(0, 500));
        return {
          success: false,
          error: `Payment provider returned an error (status ${response.status}). Please check API credentials.`,
        };
      }

      const data = await response.json();
      console.log("NOWPayments direct payment response:", JSON.stringify(data, null, 2));

      if (!response.ok) {
        console.error("NOWPayments error response:", data);
        return {
          success: false,
          error: data.message || data.error || `Payment provider error (status ${response.status})`,
        };
      }

      if (data.pay_address) {
        const qrCodeUrl = `https://api.qrserver.com/v1/create-qr-code/?size=200x200&data=${encodeURIComponent(data.pay_address)}`;

        return {
          success: true,
          paymentId: data.payment_id,
          payAddress: data.pay_address,
          payAmount: String(data.pay_amount),
          payCurrency: data.pay_currency,
          expiresAt: data.expiration_estimate_date,
          qrCodeUrl,
          status: data.payment_status,
        };
      }

      return {
        success: false,
        error: data.message || data.error || "Failed to create payment - no payment address returned",
      };
    } catch (error: any) {
      console.error("NOWPayments direct payment error:", error);
      return {
        success: false,
        error: error.message || "Payment service unavailable",
      };
    }
  }

  async getPaymentStatusFull(paymentId: string): Promise<{ status: string; payAddress?: string; payAmount?: string; payCurrency?: string } | null> {
    try {
      const apiKey = await this.getApiKey();
      const apiUrl = await this.getApiUrl();

      const response = await fetch(`${apiUrl}/payment/${paymentId}`, {
        method: "GET",
        headers: {
          "x-api-key": apiKey,
        },
      });

      if (!response.ok) {
        return null;
      }

      const data = await response.json();
      return {
        status: data.payment_status,
        payAddress: data.pay_address,
        payAmount: String(data.pay_amount),
        payCurrency: data.pay_currency,
      };
    } catch (error) {
      console.error("Error checking payment status:", error);
      return null;
    }
  }

  private sortObjectKeys(obj: any): any {
    if (obj === null || typeof obj !== 'object') {
      return obj;
    }
    if (Array.isArray(obj)) {
      return obj.map(item => this.sortObjectKeys(item));
    }
    const sortedKeys = Object.keys(obj).sort();
    const result: Record<string, any> = {};
    for (const key of sortedKeys) {
      result[key] = this.sortObjectKeys(obj[key]);
    }
    return result;
  }

  async verifyWebhook(payload: any, signature: string): Promise<boolean> {
    try {
      const ipnSecret = await storage.getSetting("NOWPAYMENTS_IPN_SECRET");
      if (!ipnSecret) {
        console.warn("NOWPayments IPN Secret not configured. Skipping signature verification.");
        return true;
      }

      if (!signature) {
        console.error("No signature provided in webhook request");
        return false;
      }

      const sortedPayload = this.sortObjectKeys(payload);
      const payloadString = JSON.stringify(sortedPayload);
      const calculatedSignature = crypto
        .createHmac("sha512", ipnSecret)
        .update(payloadString)
        .digest("hex");

      return calculatedSignature === signature;
    } catch (error) {
      console.error("Webhook verification error:", error);
      return false;
    }
  }

  async getPaymentStatus(paymentId: string): Promise<string | null> {
    try {
      const apiKey = await this.getApiKey();
      const apiUrl = await this.getApiUrl();

      const response = await fetch(`${apiUrl}/payment/${paymentId}`, {
        method: "GET",
        headers: {
          "x-api-key": apiKey,
        },
      });

      if (!response.ok) {
        return null;
      }

      const data = await response.json();
      return data.payment_status;
    } catch (error) {
      console.error("Error checking payment status:", error);
      return null;
    }
  }
}

export const nowpaymentsService = new NowPaymentsService();

client/src/pages/billing.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { 
  Receipt, 
  RefreshCw, 
  ArrowUpCircle, 
  ArrowDownCircle,
  Clock,
  CheckCircle,
  XCircle,
  AlertCircle,
} from "lucide-react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Skeleton } from "@/components/ui/skeleton";
import { Badge } from "@/components/ui/badge";
import { AddBalanceDialog } from "@/components/add-balance-dialog";

interface Transaction {
  id: string;
  type: "topup" | "purchase";
  amount: string;
  currency: string;
  status: "pending" | "completed" | "failed" | "expired" | "refunded" | "cancelled";
  paymentMethod: string | null;
  externalId: string | null;
  createdAt: string;
  updatedAt: string;
}

export default function Billing() {
  const { data: transactions, isLoading, refetch, isRefetching } = useQuery<Transaction[]>({
    queryKey: ["/api/payments/transactions"],
  });

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case "completed":
        return (
          <Badge variant="outline" className="bg-emerald-500/15 text-emerald-600 dark:text-emerald-400 border-emerald-500/20">
            <CheckCircle className="h-3 w-3 mr-1" />
            Completed
          </Badge>
        );
      case "pending":
        return (
          <Badge variant="outline" className="bg-amber-500/15 text-amber-600 dark:text-amber-400 border-amber-500/20">
            <Clock className="h-3 w-3 mr-1" />
            Pending
          </Badge>
        );
      case "failed":
        return (
          <Badge variant="outline" className="bg-red-500/15 text-red-600 dark:text-red-400 border-red-500/20">
            <XCircle className="h-3 w-3 mr-1" />
            Failed
          </Badge>
        );
      case "expired":
        return (
          <Badge variant="outline" className="bg-gray-500/15 text-gray-600 dark:text-gray-400 border-gray-500/20">
            <AlertCircle className="h-3 w-3 mr-1" />
            Expired
          </Badge>
        );
      case "refunded":
        return (
          <Badge variant="outline" className="bg-blue-500/15 text-blue-600 dark:text-blue-400 border-blue-500/20">
            <ArrowUpCircle className="h-3 w-3 mr-1" />
            Refunded
          </Badge>
        );
      default:
        return (
          <Badge variant="outline">
            {status}
          </Badge>
        );
    }
  };

  const getTypeIcon = (type: string) => {
    if (type === "topup") {
      return <ArrowUpCircle className="h-5 w-5 text-emerald-500" />;
    }
    return <ArrowDownCircle className="h-5 w-5 text-red-500" />;
  };

  const totalDeposits = transactions
    ?.filter((t) => t.type === "topup" && t.status === "completed")
    .reduce((sum, t) => sum + parseFloat(t.amount), 0) || 0;

  const pendingDeposits = transactions
    ?.filter((t) => t.type === "topup" && t.status === "pending")
    .reduce((sum, t) => sum + parseFloat(t.amount), 0) || 0;

  return (
    <div className="space-y-6">
      <div className="flex flex-col gap-4 md:flex-row md:items-center md:justify-between">
        <div>
          <h1 className="text-3xl font-semibold">Billing</h1>
          <p className="text-muted-foreground mt-1">
            View your payment history and transactions
          </p>
        </div>
        <div className="flex items-center gap-3">
          <Button
            variant="outline"
            size="icon"
            onClick={() => refetch()}
            disabled={isRefetching}
          >
            <RefreshCw className={`h-4 w-4 ${isRefetching ? "animate-spin" : ""}`} />
          </Button>
          <AddBalanceDialog />
        </div>
      </div>

      <div className="grid gap-4 md:grid-cols-2">
        <Card>
          <CardHeader className="pb-2">
            <CardDescription>Total Deposited</CardDescription>
            <CardTitle className="text-2xl font-mono">${totalDeposits.toFixed(2)}</CardTitle>
          </CardHeader>
        </Card>
        <Card>
          <CardHeader className="pb-2">
            <CardDescription>Pending Deposits</CardDescription>
            <CardTitle className="text-2xl font-mono">${pendingDeposits.toFixed(2)}</CardTitle>
          </CardHeader>
        </Card>
      </div>

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Receipt className="h-5 w-5" />
            Transaction History
          </CardTitle>
          <CardDescription>
            {transactions?.length || 0} transaction{transactions?.length !== 1 ? "s" : ""}
          </CardDescription>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="space-y-4">
              {[1, 2, 3, 4, 5].map((i) => (
                <div key={i} className="flex items-center gap-4 p-4 rounded-lg border">
                  <Skeleton className="h-10 w-10 rounded-full" />
                  <div className="flex-1 space-y-2">
                    <Skeleton className="h-4 w-32" />
                    <Skeleton className="h-3 w-48" />
                  </div>
                  <Skeleton className="h-6 w-20" />
                </div>
              ))}
            </div>
          ) : transactions && transactions.length > 0 ? (
            <div className="space-y-3">
              {transactions.map((tx) => (
                <div
                  key={tx.id}
                  className="flex flex-col md:flex-row md:items-center gap-4 p-4 rounded-lg border hover:bg-muted/50 transition-colors"
                >
                  <div className="flex items-center gap-4 flex-1 min-w-0">
                    <div className="flex h-10 w-10 items-center justify-center rounded-full bg-muted shrink-0">
                      {getTypeIcon(tx.type)}
                    </div>
                    <div className="flex-1 min-w-0">
                      <div className="flex flex-wrap items-center gap-2">
                        <span className="font-medium">
                          {tx.type === "topup" ? "Deposit" : "Purchase"}
                        </span>
                        {getStatusBadge(tx.status)}
                      </div>
                      <div className="flex flex-wrap items-center gap-2 mt-1 text-sm text-muted-foreground">
                        <span>{formatDate(tx.createdAt)}</span>
                        {tx.paymentMethod && (
                          <>
                            <span>|</span>
                            <span className="capitalize">{tx.paymentMethod}</span>
                          </>
                        )}
                        {tx.externalId && (
                          <>
                            <span>|</span>
                            <span className="font-mono text-xs">ID: {tx.externalId}</span>
                          </>
                        )}
                      </div>
                    </div>
                  </div>
                  <div className="text-right">
                    <div className={`font-mono font-semibold ${tx.type === "topup" ? "text-emerald-600 dark:text-emerald-400" : "text-red-600 dark:text-red-400"}`}>
                      {tx.type === "topup" ? "+" : "-"}${parseFloat(tx.amount).toFixed(2)}
                    </div>
                    <div className="text-xs text-muted-foreground">{tx.currency}</div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="flex flex-col items-center justify-center py-16 text-center">
              <div className="flex h-16 w-16 items-center justify-center rounded-full bg-muted mb-4">
                <Receipt className="h-8 w-8 text-muted-foreground" />
              </div>
              <h3 className="font-medium mb-1">No transactions yet</h3>
              <p className="text-sm text-muted-foreground mb-4">
                Add funds to your account to get started
              </p>
              <AddBalanceDialog />
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/payment-modal.tsx.content:
import { useState, useEffect } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Copy, Check, Loader2, QrCode, Clock, RefreshCw, ChevronRight, Mail } from "lucide-react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";

interface PaymentDetails {
  paymentId: string;
  payAddress: string;
  payAmount: string;
  payCurrency: string;
  expiresAt: string;
  qrCodeUrl: string;
  status: string;
}

interface PaymentModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  amount: number;
  onPaymentComplete: () => void;
}

const POPULAR_CURRENCIES = [
  { value: "btc", label: "Bitcoin", icon: "" },
  { value: "eth", label: "Ethereum", icon: "" },
  { value: "ltc", label: "Litecoin", icon: "" },
  { value: "usdt", label: "Tether (USDT)", icon: "" },
  { value: "trx", label: "Tron", icon: "T" },
  { value: "bnb", label: "BNB", icon: "B" },
  { value: "xrp", label: "XRP", icon: "X" },
  { value: "doge", label: "Dogecoin", icon: "" },
  { value: "sol", label: "Solana", icon: "S" },
  { value: "matic", label: "Polygon", icon: "P" },
];

export function PaymentModal({ open, onOpenChange, amount, onPaymentComplete }: PaymentModalProps) {
  const [step, setStep] = useState<"choose" | "deposit">("choose");
  const [selectedCurrency, setSelectedCurrency] = useState("btc");
  const [notifyEmail, setNotifyEmail] = useState("");
  const [paymentDetails, setPaymentDetails] = useState<PaymentDetails | null>(null);
  const [copied, setCopied] = useState<"address" | "amount" | null>(null);
  const [timeLeft, setTimeLeft] = useState<number>(0);
  const { toast } = useToast();

  const { data: currenciesData } = useQuery({
    queryKey: ["payment-currencies"],
    queryFn: async () => {
      const response = await fetch("/api/payments/currencies", {
        credentials: "include",
      });
      if (!response.ok) return { currencies: POPULAR_CURRENCIES.map(c => c.value) };
      return response.json();
    },
    enabled: open,
  });

  const createPaymentMutation = useMutation({
    mutationFn: async (): Promise<PaymentDetails> => {
      const response = await fetch("/api/payments/create-direct", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ amount, currency: "USD", payCurrency: selectedCurrency }),
        credentials: "include",
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to create payment");
      }

      return response.json();
    },
    onSuccess: (data) => {
      setPaymentDetails(data);
      setStep("deposit");
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Payment failed",
        description: error.message,
      });
    },
  });

  const { data: statusData } = useQuery({
    queryKey: ["payment-status", paymentDetails?.paymentId],
    queryFn: async () => {
      if (!paymentDetails?.paymentId) return null;
      const response = await fetch(`/api/payments/status/${paymentDetails.paymentId}`, {
        credentials: "include",
      });
      if (!response.ok) return null;
      return response.json();
    },
    enabled: !!paymentDetails?.paymentId && open && step === "deposit",
    refetchInterval: 5000,
  });

  useEffect(() => {
    if (statusData?.status === "finished" || statusData?.status === "confirmed") {
      toast({
        title: "Payment confirmed!",
        description: "Your balance has been updated.",
      });
      onPaymentComplete();
      onOpenChange(false);
    }
  }, [statusData?.status, onPaymentComplete, onOpenChange, toast]);

  useEffect(() => {
    if (!open) {
      setStep("choose");
      setPaymentDetails(null);
      setCopied(null);
      setNotifyEmail("");
    }
  }, [open]);

  useEffect(() => {
    if (paymentDetails?.expiresAt) {
      const interval = setInterval(() => {
        const now = new Date().getTime();
        const expires = new Date(paymentDetails.expiresAt).getTime();
        const diff = Math.max(0, Math.floor((expires - now) / 1000));
        setTimeLeft(diff);

        if (diff === 0) {
          clearInterval(interval);
        }
      }, 1000);

      return () => clearInterval(interval);
    }
  }, [paymentDetails?.expiresAt]);

  const copyToClipboard = async (text: string, type: "address" | "amount") => {
    try {
      await navigator.clipboard.writeText(text);
      setCopied(type);
      setTimeout(() => setCopied(null), 2000);
      toast({
        title: "Copied!",
        description: `${type === "address" ? "Wallet address" : "Amount"} copied to clipboard`,
      });
    } catch {
      toast({
        variant: "destructive",
        title: "Failed to copy",
        description: "Please copy manually",
      });
    }
  };

  const formatTime = (seconds: number) => {
    const hours = Math.floor(seconds / 3600);
    const mins = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;
    if (hours > 0) {
      return `${hours}:${mins.toString().padStart(2, "0")}:${secs.toString().padStart(2, "0")}`;
    }
    return `${mins}:${secs.toString().padStart(2, "0")}`;
  };

  const handleNextStep = () => {
    createPaymentMutation.mutate();
  };

  const getCurrencyInfo = (value: string) => {
    return POPULAR_CURRENCIES.find(c => c.value === value) || { value, label: value.toUpperCase(), icon: value[0]?.toUpperCase() };
  };

  const availableCurrencies = currenciesData?.currencies 
    ? POPULAR_CURRENCIES.filter(c => currenciesData.currencies.includes(c.value))
    : POPULAR_CURRENCIES;

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[450px]">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <QrCode className="h-5 w-5" />
            Pay ${amount} with Crypto
          </DialogTitle>
          <DialogDescription>
            {step === "choose" ? "Choose your payment method" : "Send the exact amount to complete payment"}
          </DialogDescription>
        </DialogHeader>

        <div className="flex items-center gap-2 mb-4">
          <div className={`px-3 py-1 rounded-full text-sm font-medium ${step === "choose" ? "bg-primary text-primary-foreground" : "bg-muted text-muted-foreground"}`}>
            1. Choose asset
          </div>
          <ChevronRight className="h-4 w-4 text-muted-foreground" />
          <div className={`px-3 py-1 rounded-full text-sm font-medium ${step === "deposit" ? "bg-primary text-primary-foreground" : "bg-muted text-muted-foreground"}`}>
            2. Send deposit
          </div>
        </div>

        {step === "choose" ? (
          <div className="space-y-4">
            <div className="space-y-2">
              <Label>Select cryptocurrency</Label>
              <Select value={selectedCurrency} onValueChange={setSelectedCurrency}>
                <SelectTrigger>
                  <SelectValue>
                    {(() => {
                      const curr = getCurrencyInfo(selectedCurrency);
                      return (
                        <div className="flex items-center gap-2">
                          <span className="w-6 h-6 rounded-full bg-primary/10 flex items-center justify-center text-sm font-bold">
                            {curr.icon}
                          </span>
                          <span>{curr.label}</span>
                        </div>
                      );
                    })()}
                  </SelectValue>
                </SelectTrigger>
                <SelectContent>
                  {availableCurrencies.map((currency) => (
                    <SelectItem key={currency.value} value={currency.value}>
                      <div className="flex items-center gap-2">
                        <span className="w-6 h-6 rounded-full bg-primary/10 flex items-center justify-center text-sm font-bold">
                          {currency.icon}
                        </span>
                        <span>{currency.label}</span>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div className="p-3 bg-muted/50 rounded-lg">
              <div className="text-sm text-muted-foreground">Amount to pay</div>
              <div className="text-2xl font-bold">${amount.toFixed(2)}</div>
            </div>

            <div className="border-t pt-4 space-y-3">
              <div className="flex items-center gap-2 text-sm text-muted-foreground">
                <Mail className="h-4 w-4" />
                <span>Get notified when payment is received</span>
              </div>
              <div className="flex gap-2">
                <Input
                  type="email"
                  placeholder="Email for status updates"
                  value={notifyEmail}
                  onChange={(e) => setNotifyEmail(e.target.value)}
                  className="flex-1"
                />
              </div>
            </div>

            <Button 
              onClick={handleNextStep} 
              className="w-full" 
              size="lg"
              disabled={createPaymentMutation.isPending}
            >
              {createPaymentMutation.isPending ? (
                <>
                  <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                  Creating payment...
                </>
              ) : (
                <>
                  Next step
                  <ChevronRight className="h-4 w-4 ml-2" />
                </>
              )}
            </Button>
          </div>
        ) : paymentDetails ? (
          <div className="space-y-4">
            {timeLeft > 0 && (
              <div className="flex items-center justify-center gap-2 text-sm text-muted-foreground bg-muted/50 rounded-lg py-2">
                <Clock className="h-4 w-4" />
                <span>Time remaining: <strong className="text-foreground">{formatTime(timeLeft)}</strong></span>
              </div>
            )}

            <div className="flex flex-col items-center p-4 bg-white rounded-lg border">
              <img 
                src={paymentDetails.qrCodeUrl} 
                alt="Payment QR Code" 
                className="w-48 h-48"
              />
            </div>

            <div className="space-y-3">
              <div className="space-y-1.5">
                <label className="text-sm font-medium text-muted-foreground">Amount to send</label>
                <div className="flex items-center gap-2">
                  <div className="flex-1 p-3 bg-muted rounded-lg">
                    <div className="font-mono text-lg font-bold">
                      {paymentDetails.payAmount} {paymentDetails.payCurrency.toUpperCase()}
                    </div>
                    <div className="text-sm text-muted-foreground">~ ${amount}</div>
                  </div>
                  <Button
                    variant="outline"
                    size="icon"
                    onClick={() => copyToClipboard(paymentDetails.payAmount, "amount")}
                  >
                    {copied === "amount" ? <Check className="h-4 w-4 text-green-500" /> : <Copy className="h-4 w-4" />}
                  </Button>
                </div>
              </div>

              <div className="space-y-1.5">
                <label className="text-sm font-medium text-muted-foreground">Send to address</label>
                <div className="flex items-center gap-2">
                  <div className="flex-1 p-3 bg-muted rounded-lg font-mono text-xs break-all">
                    {paymentDetails.payAddress}
                  </div>
                  <Button
                    variant="outline"
                    size="icon"
                    onClick={() => copyToClipboard(paymentDetails.payAddress, "address")}
                  >
                    {copied === "address" ? <Check className="h-4 w-4 text-green-500" /> : <Copy className="h-4 w-4" />}
                  </Button>
                </div>
              </div>
            </div>

            <div className="flex items-center justify-center gap-2 text-sm text-muted-foreground pt-2">
              <RefreshCw className={`h-4 w-4 ${statusData ? "animate-spin" : ""}`} />
              <span>Waiting for payment confirmation...</span>
            </div>

            <div className="text-center text-xs text-muted-foreground pt-2">
              Powered by <span className="font-semibold">NOWPayments</span>
            </div>
          </div>
        ) : null}
      </DialogContent>
    </Dialog>
  );
}

server/encryption.ts.content:
import { createCipheriv, createDecipheriv, randomBytes, scryptSync } from "crypto";

const ALGORITHM = "aes-256-gcm";
const KEY_LENGTH = 32;
const IV_LENGTH = 16;
const AUTH_TAG_LENGTH = 16;

function getEncryptionKey(): Buffer {
  const secret = process.env.VPS_CREDENTIALS_SECRET || process.env.SESSION_SECRET || "default-key-for-dev";
  return scryptSync(secret, "salt", KEY_LENGTH);
}

export function isEncrypted(text: string): boolean {
  if (!text) return false;
  const parts = text.split(":");
  return parts.length === 3 && parts[0].length === IV_LENGTH * 2 && parts[1].length === AUTH_TAG_LENGTH * 2;
}

export function encryptCredential(plaintext: string): string {
  if (!plaintext) return plaintext;
  if (isEncrypted(plaintext)) return plaintext;

  const key = getEncryptionKey();
  const iv = randomBytes(IV_LENGTH);
  const cipher = createCipheriv(ALGORITHM, key, iv);

  let encrypted = cipher.update(plaintext, "utf8", "hex");
  encrypted += cipher.final("hex");
  const authTag = cipher.getAuthTag();

  return `${iv.toString("hex")}:${authTag.toString("hex")}:${encrypted}`;
}

export function decryptCredential(ciphertext: string): string {
  if (!ciphertext || !ciphertext.includes(":")) return ciphertext;

  try {
    const [ivHex, authTagHex, encrypted] = ciphertext.split(":");
    if (!ivHex || !authTagHex || !encrypted) return ciphertext;

    const key = getEncryptionKey();
    const iv = Buffer.from(ivHex, "hex");
    const authTag = Buffer.from(authTagHex, "hex");
    const decipher = createDecipheriv(ALGORITHM, key, iv);
    decipher.setAuthTag(authTag);

    let decrypted = decipher.update(encrypted, "hex", "utf8");
    decrypted += decipher.final("utf8");

    return decrypted;
  } catch {
    return ciphertext;
  }
}
